fl:window.script:


int setter(const void* value){
    $log(LOG_LOG,"Setter");
    return 0;
}

int getter(void** value){
    $log(LOG_LOG,"Getter");
    return 0;
}

void on_set(){
    if($set("foo",0)!=0)$log(LOG_PANIC,"Setter failed");
}

void on_get(){
    char* ptr = 0;
    if($get("foo",ptr)!=0)$log(LOG_PANIC,"Getter failed");
}

$field(foo,setter,getter);
$cb(on_set)
$cb(on_get)
